document.addEventListener("DOMContentLoaded",function(){const a=document.getElementById("nav-toggle"),t=document.getElementById("nav-list"),e=document.getElementById("nav-overlay"),i=document.getElementById("search-toggle"),n=document.querySelector(".nav__search");function c(){return window.innerWidth<=850}function o(){c()?(t.classList.toggle("active"),e.classList.toggle("active"),n.classList.remove("active")):(t.classList.remove("active"),e.classList.remove("active"))}function s(){c()&&n.classList.toggle("active")}a.addEventListener("click",function(){o()}),e.addEventListener("click",function(){o()}),i.addEventListener("click",function(){s()})});
